"use strict";


if (typeof console == "undefined") {
    window.console = { log: function () {} };
}


var pollIds = [];
var pollOptions = {};
var signals = {};
var discardVote = false;
smokesignals.convert(signals);
google.setOnLoadCallback(initGraphs);


$(function() {
    signals.emit('ready');

    $(document).on('focus',     '.js-signals-focus',    triggerSignals);
    $(document).on('click',     '.js-signals-click',    triggerSignals);
    $(document).on('submit',    '.js-signals-submit',   triggerSignals);
    $(document).on('click',     'a.external',           handleExternalLink);

    makeMoments();
    setInterval(makeMoments, 30 * 1000);
    $("img").unveil(200);

    if ($('meta[name="poll-id"]').attr('content')) {

    }

    $('.get-search').popover();
    $('.get-search').on('click', function(e) {
        e.preventDefault();
    });
});


/* Thanks MDN */
function partial(fn /*, args...*/) {
    var aps = Array.prototype.slice,
        args = aps.call(arguments, 1);

    return function() {
        return fn.apply(this, args.concat(aps.call(arguments)));
    };
}


function handleExternalLink(event) {
    var $target = $(event.currentTarget);
    if ($target.attr('href').indexOf('https://polls.abrah.am') === 0 ||
        $target.attr('href').indexOf('http://dev.abrah.am') === 0) {
        // noop
    } else {
        $(event.currentTarget).attr('target', '_blank');
    }
}

function triggerSignals(event) {
    var $target = $(event.currentTarget);
    var data = $target.data();
    signals.emit(data['signalsName'], data);
    console.log(data['signalsName'], data);

    if (data['signalsOnce']) {
        $target
            .removeClass('js-signals-focus')
            .removeClass('js-signals-click')
            .removeClass('js-signals-submit');
    }

    if (event.type == 'submit' || (event.type == 'click' && event.currentTarget.localName == 'a')) {
        event.preventDefault();
    }
}


/**
 * Create polls
 */
signals.on('create-unhide-next-input', unhideNextOption)


function unhideNextOption() {
    $('.form-polls-create input.hidden:first').hide().removeClass('hidden').slideDown();
}


/**
 * Utils
 */
function makeMoments(){
    $('.datetime').each(function(index, elem) {
        var $elem = $(elem);
        var fromNow = moment.utc($elem.data('datetime')).fromNow();
        $elem.text(fromNow);
    });
}


/**
 * Stars
 */
signals.on('star-poll', postPollsIdStarsAPI);
signals.on('star-poll', postPollsIdStarsUI);
signals.on('unstar-poll', deletePollsIdStarsUI);


function postPollsIdStarsAPI(options) {
    var done = function(){};
    var fail = partial(postPollsIdStarsAPIFail, options);
    var path = '/polls/' + options.pollId + '/stars'
    postAPI(path, {}, done, fail);
}


function postPollsIdStarsAPIFail(options, data, textStatus, jqXHR) {
    signals.emit('unstar-poll', options);

    if (data.status == 400) {
        alert(data.responseText);
        return;
    }

    alert('Something went wrong.');
    return;
}


function postPollsIdStarsUI(options) {
    $('.js-polls-stars-' + options.pollId + ' span')
        .addClass('glyphicon-star')
        .addClass('adn-action-take')
        .removeClass('glyphicon-star-empty');
}


function deletePollsIdStarsUI(options) {
    $('.js-polls-stars-' + options.pollId + ' span')
        .addClass('glyphicon-star-empty')
        .removeClass('adn-action-take')
        .removeClass('glyphicon-star');
}


